from __future__ import absolute_import


from pygui.item.core import ActionItem
from pygui.actions.jobs.common import push_search_menu
from pygui.actions.jobs.dailymotion import ChangeDailyMotionVideoFormatItem
from pygui.shared import pygui_globs


name = 'DailyMotion'
image = 'hand_selected'
category = 'dailymotion'
position = None
default = True
unique = True


def is_available(context):
	return True

def get_action_info_list(context):
	return[ActionItem(_('Search'), type_='action', action=push_search_menu, args=[context, pygui_globs['menustack']]),
	ChangeDailyMotionVideoFormatItem(_('Choose video format'), context['selected'], context['menu'])]
