from pygui.facilities.picasa_config import PicasaConfig
from pygui.shared import pygui_globs



# Del a private user from picasa config file.
def execute_del_picasa_private_user(context):
	item = context['selected']
	PicasaConfig().DelPrivateUser(item.email)
	context['parent']._items = None
	pygui_globs['menustack'].back_to_menu(context['menu'])
	context['menu'].reload_func()
	return None



# Del a public user from picasa config file.
def execute_del_picasa_public_user(context):
	item = context['selected']
	PicasaConfig().DelPublicUser(item.username)
	context['parent']._items = None
	pygui_globs['menustack'].back_to_menu(context['menu'])
	context['menu'].reload_func()
	return None
