from pygui.facilities.searchsave import ShoutCastConfig
from pygui.shared import pygui_globs



# Del a search from shoutcast config file.
def execute_del_shoutcast_search(context):
	item = context['selected']
	ShoutCastConfig().DelSearch(item.name)
	context['parent']._items = None
	pygui_globs['menustack'].back_to_menu(context['menu'])
	context['menu'].reload_func()
	return None
