from __future__ import absolute_import

from peewee.debug import DEBUG
from pygui.menu.players import TVPlayer


def schedule_record(context, prev_menu, stack):
	stack.back_one_menu(refresh=False)
	context.player.schedule_record()
	return None


def display_epg(context, stack):
	stack.back_one_menu()
	context.display_epg()
	return None


def goto_schedule_record(context, prev_menu, stack):
	stack.back_one_menu()
	context.player.goto_schedule_record()
	return None
