from __future__ import absolute_import

from pygui.window import KeyboardWindow
from pygui.item.web import URLItem


def execute_goto(context):
	def __do_goto(kbd):
		browser = context['menu']
		if not kbd.text:
			pass
		url = URLItem(kbd.text, url=kbd.text, menu=browser)
		browser.play(url)
		kbd.hide()
		return None
	kbd = KeyboardWindow(_('Enter url'), text='http://', confirm_action=__do_goto)
	kbd.show()
	return None


def execute_bookmark(context):
	browser = context['menu']
	browser.bookmark_current()
	return None
