from __future__ import absolute_import

from peewee.notifier import Event

WHEEL_RWD = Event('WHEEL_RWD')
WHEEL_FWD = Event('WHEEL_FWD')
LEFT = Event('LEFT')
RIGHT = Event('RIGHT')
UP = Event('UP')
DOWN = Event('DOWN')
SELECT = Event('SELECT')
TOGGLE_MENU = Event('TOGGLE_MENU')
ACTION_MENU = Event('ACTION_MENU')
VOLUME_UP = Event('VOLUME_UP')
VOLUME_DOWN = Event('VOLUME_DOWN')
MUTE = Event('MUTE')
RECORD = Event('RECORD')
STOP = Event('STOP')
INFO = Event('INFO')
HOME = Event('HOME')
MARKER = Event('MARKER')
POWER = Event('POWER')
EXIT = Event('EXIT')
SLEEP = Event('SLEEP')
DEBUG = Event('DEBUG')



events = {}
__all__ = ['Event']



for name, obj in locals().items():
	if isinstance(obj, Event):
		events[name] = obj
		__all__.append(name)
