from __future__ import absolute_import

from pygui.eventmanager.menu.core import MenuEventHandler


class WifiSelectMenuEventHandler(MenuEventHandler):
	
	def event_select(self, event):
		self.player.execute_selected()
		return True

	def event_stop(self, event):
		self.player.hide()
		return True

	def event_left(self, event):
		return True

	event_right, event_toggle_menu = event_left, event_left


