from __future__ import absolute_import
from optparse import OptionParser

usage = 'usage: %prog [options] arg'


parser = OptionParser(usage)
parser.add_option('-l', '--logger', dest='logger_name', action='append', type='string', help='logger used during execution')
parser.add_option('-d', '--debug', action='store_const', const=True, dest='debug', help='stop on debug (see facilities/debug.py)')
parser.add_option('--debug_mode', default='')
parser.add_option('--logger_mode', default='WARNING', help="default logger don't display DEBUG level [DEBUG, INFO, WARNING, ERROR, CRITICAL]")
