from wyvas import Container


class Cursor(Container):
	
	def __init__(self):
		Container.__init__(self)
		return None

	def update(self, *args, **kwargs):
		if 'send_mouse' == kwargs['action']:
			self.animate('move', left=kwargs['x'], top=kwargs['y'], duration=0)
		return None
