from __future__ import absolute_import
from pygui.gui.widget.listing.textitem import TextItem
from pygui.gui.widget.listing.icontextitem import IconTextItem
from pygui.gui.widget.listing.imageitem import ImageItem
from pygui.gui.widget.listing.thumbnailitem import ThumbnailItem
from pygui.config import plugins


# TODO Rename this, the name clashes with
# pygui.item.containers.ItemList
class ItemList(list):

	def __init__(self, list_, start=0, end=0, selected=0):
		list.__init__(self, list_)
		self.start = start
		self.end = end
		self.selected = selected
		return None

	def __str__(self):
		return 'start : %s \tselected : %s\tend : %s' % (self.start, self.selected, self.end)


class_item_name_dict = dict(text=TextItem, icon_text=IconTextItem, image=ImageItem, thumbnail=ThumbnailItem)


if 'tv' in plugins:
	from pygui.gui.widget.listing.epgitem import EPGItem
	class_item_name_dict['epg'] = EPGItem


__all__ = ['class_item_name_dict']
