from __future__ import absolute_import

import wyvas


# Specialization of the wyvas Viewer that can dispatch
# rotation-inducing Louie signals.
class Viewer(wyvas.Container):

	def update(self, *args, **kwargs):
		if 'rotate' in kwargs:
			self.rotate(kwargs['rotate'])
		else:
			wyvas.Viewer.update(self, *args, **kwargs)
		return None
