from __future__ import absolute_import

__all__ = ['ParametersSetupItem', 'UserConfigSetupItem', 'UserConfigSetupItem', 'IterItemContainer', 'BooleanUserConfigSetupItem']

from peewee.messages import send as louie_send
from pygui.item.parameters.core import ParametersSetupItem, UserConfigSetupItem, UserConfigSetupMultipleItem, IterItemContainer, BooleanUserConfigSetupItem

def _update_lists(func):
	def _exec(*args, **kw):
		func(*args, **kw)
		self = args[0]
		louie_send('preview_list', sender=self.menu, action='reset', value=self.menu['preview_list'])
		louie_send('options_list', sender=self.menu, action='reset', value=self.menu['options_list'])
		return None

	return _exec
