from __future__ import absolute_import

from pygui.facilities.l10n import UnicodeDict
from pygui.item.parameters import UserConfigSetupItem, ParametersSetupItem

SYSTEM_ITEM_BOOL = UnicodeDict({True: 'Yes', False: 'No'})


class NetworkSleepSetupItem(UserConfigSetupItem):

	translation_dict = SYSTEM_ITEM_BOOL



class HdmiCECSetupItem(UserConfigSetupItem):

	translation_dict = SYSTEM_ITEM_BOOL



class PreviewFolderSetupItem(UserConfigSetupItem):

	translation_dict = SYSTEM_ITEM_BOOL



class SystemConfigurationItem(ParametersSetupItem):

	depth = 3

	def __init__(self, *args, **kw):
		ParametersSetupItem.__init__(self, *args, **kw)
		return None

	def browse(self, preview=False):
		preview_list = [NetworkSleepSetupItem(name='Power button sleep network', menu=self.menu, domain='power', key='network_sleep', choices_list=sorted(SYSTEM_ITEM_BOOL, reverse=False)),
										HdmiCECSetupItem(name='Enable HDMI-CEC communication', menu=self.menu, domain='advanced', key='hdmi_cec', choices_list=sorted(SYSTEM_ITEM_BOOL, reverse=False)),
										PreviewFolderSetupItem(name='Preview folder content', menu=self.menu, domain='advanced', key='preview_folder', choices_list=sorted(SYSTEM_ITEM_BOOL, reverse=False))]
		return preview_list
