from __future__ import absolute_import

from pygui.item.containers import GenericContainer
from pygui.item.parameters.network.networksetup import NetworkWireSetupItem, StatusNetworkConfSetupItem



def getitem(menu):
	return NetworkSetupItem(_('Network'), 'networksetup', display_type='setup_network', menu=menu)



# Network setup root.
class NetworkSetupItem(GenericContainer):

	def browse(self, preview=False):
		return [StatusNetworkConfSetupItem(name=_('Connection information'), can_modify=False, menu=self, type_='setupitem'),
						NetworkWireSetupItem(name=_('Configure connection'), menu=self.menu)]

	def update_name(self):
		self._name = _('Network')
		return None
