from __future__ import absolute_import

from pygui.item.containers import GenericContainer
from pygui.item.parameters import ParametersSetupItem
from pygui.item.parameters.videophoto.video import VideoParametersSetupItem, YoutubeParametersSetupItem, DailyMotionParametersSetupItem, TVReplayParametersSetupItem
from pygui.item.parameters.videophoto.photo import SlideShowParametersSetupItem

def getitem(menu):
	return VideoPhotoParametersSetupItem(_('Video/Photo'), 'video', display_type='setup_video', menu=menu)



class VideoPhotoParametersSetupItem(GenericContainer):
	def browse(self, preview=False):
		return [VideoParametersSetupItem('Video parameters', type_='dvdsetup'),
		YoutubeParametersSetupItem('Youtube parameters', type_='dvdsetup'),
		DailyMotionParametersSetupItem('DailyMotion parameters', type_='dvdsetup'),
		TVReplayParametersSetupItem('TVReplay parameters', type_='dvdsetup'),
		SlideShowParametersSetupItem('Slideshow parameters', type_='dvdsetup')]

	def update_name(self):
		self._name = _('Video/Photo')
		return None
