from pygui.facilities.codemapping import transition_dict
from pygui.item.parameters import ParametersSetupItem, BooleanUserConfigSetupItem, UserConfigSetupItem

import pygui.config as config



class TransitionParametersSetupItem(UserConfigSetupItem):

	translation_dict = transition_dict



class SlideShowParametersSetupItem(ParametersSetupItem):

	depth = 3

	def __init__(self, *args, **kw):
		ParametersSetupItem.__init__(self, *args, **kw)
		self.preview_list = [TransitionParametersSetupItem('photo', 'transition', config.HMI_PHOTO['transitions'], 'Transition', type_='setupitem')]
		return None
