from __future__ import absolute_import


# This class permits to import on the fly module containing markerlist actions.
# These modules are in the actions/ folder, and are named as this convention :
#     'marker_*action*.py', *action* can be 'delete' to delete marked items,
#         'rename' to rename a marked item, ...
class MarkerActionsGetter(object):

	def __init__(self):
		self._MarkerActionsGetter__actions = dict()
		return None

	# Getting an action permits to import the module containing its implementations
	def __getitem__(self, action_name):
		act = self._MarkerActionsGetter__actions
		if action_name not in act:
			act[action_name] = __import__(action_name, globals(), {}, ('markerlist.actions',))
		return act[action_name]


actions = MarkerActionsGetter()


__all__ = ['actions']
