from __future__ import absolute_import
from marker_dup_to_highlight import is_compatible as dup_is_compatible, execute as dup_execute
from marker_delete import is_compatible as del_is_compatible, execute as del_execute


caption = 'Move marked items into "%(highlighted_item)s"'


def is_compatible(context):
	if dup_is_compatible(context):
		del_is_compatible(context)
	return False


def execute(context):
	dup_execute(context)
	del_execute(context)
	return None
