from __future__ import absolute_import
from pygui.markerlist import get_marker_from_item, markerlists
from peewee.debug import DEBUG, log

caption = 'Play marker list'


def is_compatible(context):
	return context['menu'].type in ('video_image', 'image', 'video', 'audio')


# Play all playable marked items
def execute(context):
	fl = markerlists().get(context['menu'].universe)
	context['parent'].show_menu()
	try:
		context['menu'].stop()
	except:
		pass
	finally:
		fl.play()
	return None
