from wydbus import WyDbus, WDB_AS_THREAD
from threading import Thread, Event
import os
import time
import urllib
from subprocess import Popen

__all__ = ['Wydvd', 'WydvdException']



class WydvdDbusThread(Thread):

	def __init__(self, dbus):
		Thread.__init__(self)
		self.dbus = dbus
		self.user_stop = False
		return None

	def __del__(self):
		self.stop()
		return None

	def stop(self):
		self.user_stop = True
		return None

	def run(self):
		while self.user_stop == False:
			self.dbus.step()
		return None



class WydvdException(Exception):
	__module__ = __name__



# WyDVD client interface.
#
# Example usage:
#  >> from wydvd import Wydvd
#  >> wdd = Wydvd()
#  >> wdd.open('/media/DVD-ISO/Orgazmo.iso')
#  >> wdd.play()
#  >> wdd.stop()
#  >> wdd.close()
