class ExternalLed(object):

	def __init__(self, wydb):
		self.wdb = wydb
		led_dbus_path = '/com/wyplay/wycrs/wylight'
		led_dbus_interface = 'com.wyplay.wycrs'
		self.recordStarts = self.wdb.signal(led_dbus_path, 'recordStarts', '', led_dbus_interface)
		self.recordEnds = self.wdb.signal(led_dbus_path, 'recordEnds', '', led_dbus_interface)
		return None
